package university.management.system;import javax.swing.*;import java.awt.*;import java.awt.event.*;import java.sql.*;public class EnterMarks extends JFrame implements ActionListener {    Choice choicerollno;    JComboBox<String> comboBox;    JTextField sub1, sub2, sub3, sub4, sub5, mrk1, mrk2, mrk3, mrk4, mrk5;    JButton submit, cancel;    EnterMarks() {        getContentPane().setBackground(new Color(252, 245, 210));        ImageIcon i1 = new ImageIcon(ClassLoader.getSystemResource("icon/exam.png"));        Image i2 = i1.getImage().getScaledInstance(400, 300, Image.SCALE_DEFAULT);        ImageIcon i3 = new ImageIcon(i2);        JLabel img = new JLabel(i3);        img.setBounds(500, 40, 400, 300);        add(img);        JLabel heading = new JLabel("Enter Marks of Student");        heading.setBounds(50, 0, 500, 50);        heading.setFont(new Font("Tahoma", Font.BOLD, 20));        add(heading);        JLabel rollno = new JLabel("Select Roll Number");        rollno.setBounds(50, 70, 150, 20);        add(rollno);        choicerollno = new Choice();        choicerollno.setBounds(200, 70, 150, 20);        add(choicerollno);        try {            Conn c = new Conn();            ResultSet resultSet = c.statement.executeQuery("SELECT rollno FROM student");            while (resultSet.next()) {                choicerollno.add(resultSet.getString("rollno"));            }        } catch (Exception e) {            e.printStackTrace();        }        JLabel sem = new JLabel("Select Semester");        sem.setBounds(50, 110, 150, 20);        add(sem);        String[] semester = {                "1st Semester", "2nd Semester", "3rd Semester", "4th Semester",                "5th Semester", "6th Semester", "7th Semester", "8th Semester"        };        comboBox = new JComboBox<>(semester);        comboBox.setBounds(200, 110, 150, 20);        comboBox.setBackground(Color.WHITE);        add(comboBox);        JLabel entersub = new JLabel("Enter Subject");        entersub.setBounds(100, 150, 200, 40);        add(entersub);        JLabel entermarks = new JLabel("Enter Marks");        entermarks.setBounds(320, 150, 200, 40);        add(entermarks);        sub1 = new JTextField(); sub1.setBounds(50, 200, 200, 20); add(sub1);        sub2 = new JTextField(); sub2.setBounds(50, 230, 200, 20); add(sub2);        sub3 = new JTextField(); sub3.setBounds(50, 260, 200, 20); add(sub3);        sub4 = new JTextField(); sub4.setBounds(50, 290, 200, 20); add(sub4);        sub5 = new JTextField(); sub5.setBounds(50, 320, 200, 20); add(sub5);        mrk1 = new JTextField(); mrk1.setBounds(250, 200, 200, 20); add(mrk1);        mrk2 = new JTextField(); mrk2.setBounds(250, 230, 200, 20); add(mrk2);        mrk3 = new JTextField(); mrk3.setBounds(250, 260, 200, 20); add(mrk3);        mrk4 = new JTextField(); mrk4.setBounds(250, 290, 200, 20); add(mrk4);        mrk5 = new JTextField(); mrk5.setBounds(250, 320, 200, 20); add(mrk5);        submit = new JButton("Submit");        submit.setBounds(70, 360, 150, 25);        submit.setBackground(Color.black);        submit.setForeground(Color.WHITE);        submit.addActionListener(this);        add(submit);        cancel = new JButton("Cancel");        cancel.setBounds(280, 360, 150, 25);        cancel.setBackground(Color.black);        cancel.setForeground(Color.WHITE);        cancel.addActionListener(this);        add(cancel);        setSize(1000, 500);        setLayout(null);        setLocation(300, 150);        setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == submit) {            try {                Conn c = new Conn();                String q = "INSERT INTO marks (student_rollno, semester, subject1, subject2, subject3, subject4, subject5, marks1, marks2, marks3, marks4, marks5) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";                PreparedStatement pst = c.connection.prepareStatement(q);                pst.setString(1, choicerollno.getSelectedItem());                pst.setString(2, comboBox.getSelectedItem().toString());                pst.setString(3, sub1.getText());                pst.setString(4, sub2.getText());                pst.setString(5, sub3.getText());                pst.setString(6, sub4.getText());                pst.setString(7, sub5.getText());                pst.setInt(8, Integer.parseInt(mrk1.getText()));                pst.setInt(9, Integer.parseInt(mrk2.getText()));                pst.setInt(10, Integer.parseInt(mrk3.getText()));                pst.setInt(11, Integer.parseInt(mrk4.getText()));                pst.setInt(12, Integer.parseInt(mrk5.getText()));                pst.executeUpdate();                JOptionPane.showMessageDialog(null, "Marks Inserted Successfully!");                setVisible(false);            } catch (Exception ex) {                ex.printStackTrace();                JOptionPane.showMessageDialog(null, "Error: " + ex.getMessage());            }        } else {            setVisible(false);        }    }    public static void main(String[] args) {        new EnterMarks();    }}