package university.management.system;import javax.swing.*;import java.awt.*;import java.awt.event.*;import java.sql.*;public class updateStudent extends JFrame implements ActionListener {    JTextField textAddress, textPhone, textemail, textAadhar, textcourse, textbranch;    JLabel empText, textName, textfather, dobdob, textM10, textM12;    JButton submit, cancel;    Choice cEMPID;    updateStudent() {        getContentPane().setBackground(new Color(230, 210, 252));        JLabel heading = new JLabel("Update Student Details");        heading.setBounds(50, 10, 500, 50);        heading.setFont(new Font("serif", Font.BOLD, 35));        add(heading);        JLabel empID = new JLabel("Select Roll Number");        empID.setBounds(50, 100, 200, 20);        empID.setFont(new Font("serif", Font.PLAIN, 20));        add(empID);        cEMPID = new Choice();        cEMPID.setBounds(250, 100, 200, 20);        add(cEMPID);        try {            Conn c = new Conn();            ResultSet rs = c.statement.executeQuery("select * from student");            while (rs.next()) {                cEMPID.add(rs.getString("rollno"));            }        } catch (Exception e) {            e.printStackTrace();        }        JLabel name = new JLabel("Name");        name.setBounds(50, 150, 100, 30);        name.setFont(new Font("serif", Font.BOLD, 20));        add(name);        textName = new JLabel();        textName.setBounds(200, 150, 150, 30);        add(textName);        JLabel fname = new JLabel("Father Name");        fname.setBounds(400, 150, 200, 30);        fname.setFont(new Font("serif", Font.BOLD, 20));        add(fname);        textfather = new JLabel();        textfather.setBounds(600, 150, 150, 30);        add(textfather);        JLabel EMPIDD = new JLabel("Roll Number");        EMPIDD.setBounds(50, 200, 200, 30);        EMPIDD.setFont(new Font("serif", Font.BOLD, 20));        add(EMPIDD);        empText = new JLabel();        empText.setBounds(200, 200, 150, 30);        empText.setFont(new Font("serif", Font.BOLD, 20));        add(empText);        JLabel dob = new JLabel("Date of Birth");        dob.setBounds(400, 200, 200, 30);        dob.setFont(new Font("serif", Font.BOLD, 20));        add(dob);        dobdob = new JLabel();        dobdob.setBounds(600, 200, 150, 30);        add(dobdob);        JLabel address = new JLabel("Address");        address.setBounds(50, 250, 200, 30);        address.setFont(new Font("serif", Font.BOLD, 20));        add(address);        textAddress = new JTextField();        textAddress.setBounds(200, 250, 150, 30);        add(textAddress);        JLabel phone = new JLabel("Phone");        phone.setBounds(400, 250, 200, 30);        phone.setFont(new Font("serif", Font.BOLD, 20));        add(phone);        textPhone = new JTextField();        textPhone.setBounds(600, 250, 150, 30);        add(textPhone);        JLabel email = new JLabel("Email");        email.setBounds(50, 300, 200, 30);        email.setFont(new Font("serif", Font.BOLD, 20));        add(email);        textemail = new JTextField();        textemail.setBounds(200, 300, 150, 30);        add(textemail);        JLabel M10 = new JLabel("Class X (%)");        M10.setBounds(400, 300, 200, 30);        M10.setFont(new Font("serif", Font.BOLD, 20));        add(M10);        textM10 = new JLabel();        textM10.setBounds(600, 300, 150, 30);        add(textM10);        JLabel M12 = new JLabel("Class XII (%)");        M12.setBounds(50, 350, 200, 30);        M12.setFont(new Font("serif", Font.BOLD, 20));        add(M12);        textM12 = new JLabel();        textM12.setBounds(200, 350, 150, 30);        add(textM12);        JLabel AadharNo = new JLabel("Aadhar Number");        AadharNo.setBounds(400, 350, 200, 30);        AadharNo.setFont(new Font("serif", Font.BOLD, 20));        add(AadharNo);        textAadhar = new JTextField();        textAadhar.setBounds(600, 350, 150, 30);        add(textAadhar);        JLabel Qualification = new JLabel("Course");        Qualification.setBounds(50, 400, 200, 30);        Qualification.setFont(new Font("serif", Font.BOLD, 20));        add(Qualification);        textcourse = new JTextField();        textcourse.setBounds(200, 400, 150, 30);        add(textcourse);        JLabel Department = new JLabel("Branch");        Department.setBounds(400, 400, 200, 30);        Department.setFont(new Font("serif", Font.BOLD, 20));        add(Department);        textbranch = new JTextField();        textbranch.setBounds(600, 400, 150, 30);        add(textbranch);        loadStudentDetails(cEMPID.getSelectedItem());        cEMPID.addItemListener(e -> loadStudentDetails(cEMPID.getSelectedItem()));        submit = new JButton("Update");        submit.setBounds(250, 550, 120, 30);        submit.setBackground(Color.black);        submit.setForeground(Color.white);        submit.addActionListener(this);        add(submit);        cancel = new JButton("Cancel");        cancel.setBounds(450, 550, 120, 30);        cancel.setBackground(Color.black);        cancel.setForeground(Color.white);        cancel.addActionListener(this);        add(cancel);        setSize(900, 700);        setLocation(350, 50);        setLayout(null);        setVisible(true);    }    private void loadStudentDetails(String rollno) {        try {            Conn c = new Conn();            String query = "select * from student where rollno = '" + rollno + "'";            ResultSet resultSet = c.statement.executeQuery(query);            if (resultSet.next()) {                textName.setText(resultSet.getString("name"));                textfather.setText(resultSet.getString("fname"));                dobdob.setText(resultSet.getString("dob"));                textAddress.setText(resultSet.getString("address"));                textPhone.setText(resultSet.getString("phone"));                textemail.setText(resultSet.getString("email"));                textM10.setText(resultSet.getString("class10_percent"));                textM12.setText(resultSet.getString("class12_percent"));                textAadhar.setText(resultSet.getString("aadhar"));                empText.setText(resultSet.getString("rollno"));                textcourse.setText(resultSet.getString("course"));                textbranch.setText(resultSet.getString("department"));            }        } catch (Exception e) {            e.printStackTrace();        }    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == submit) {            String rollno = empText.getText();            String address = textAddress.getText();            String phone = textPhone.getText();            String email = textemail.getText();            String aadhar = textAadhar.getText();            String course = textcourse.getText();            String department = textbranch.getText();            try {                String query = "UPDATE student SET address = ?, phone = ?, email = ?, aadhar = ?, course = ?, department = ? WHERE rollno = ?";                Conn c = new Conn();                PreparedStatement ps = c.connection.prepareStatement(query);                ps.setString(1, address);                ps.setString(2, phone);                ps.setString(3, email);                ps.setString(4, aadhar);                ps.setString(5, course);                ps.setString(6, department);                ps.setString(7, rollno);                ps.executeUpdate();                JOptionPane.showMessageDialog(null, "Student Details Updated Successfully!");                setVisible(false);            } catch (Exception ex) {                ex.printStackTrace();            }        } else {            setVisible(false);        }    }    public static void main(String[] args) {        new updateStudent();    }}